import React, { useState, useRef, useEffect } from 'react';
import { FaMicrophone, FaMicrophoneSlash, FaPlay, FaSpinner } from 'react-icons/fa';
import { AUDIO_CONFIG } from './config/constants';
import './AudioRecorder.css';
import { convertToWav } from './utils/audioConverter';

const AudioRecorder = () => {
  const [isRecording, setIsRecording] = useState(false);
  const [audioChunks, setAudioChunks] = useState([]);
  const [silenceDetected, setSilenceDetected] = useState(false);
  const [silenceStartTime, setSilenceStartTime] = useState(null);
  const [transcription, setTranscription] = useState(null);
  const [isPlaying, setIsPlaying] = useState(false);
  const [isLoading, setIsLoading] = useState(false);
  
  const mediaRecorderRef = useRef(null);
  const streamRef = useRef(null);
  const analyserRef = useRef(null);
  const audioContextRef = useRef(null);
  const silenceTimeoutRef = useRef(null);
  const audioPlayerRef = useRef(null);

  const startRecording = async () => {
    try {
      const stream = await navigator.mediaDevices.getUserMedia({ audio: true });
      streamRef.current = stream;
      
      const audioContext = new (window.AudioContext || window.webkitAudioContext)();
      audioContextRef.current = audioContext;
      
      const analyser = audioContext.createAnalyser();
      analyserRef.current = analyser;
      analyser.smoothingTimeConstant = AUDIO_CONFIG.SMOOTHING_TIME_CONSTANT;
      analyser.fftSize = 2048;
      
      const source = audioContext.createMediaStreamSource(stream);
      source.connect(analyser);
      
      // Get supported MIME type
      const mimeType = [
        'audio/webm;codecs=opus',
        'audio/webm',
        'audio/ogg;codecs=opus',
        'audio/ogg'
      ].find(type => MediaRecorder.isTypeSupported(type));
      
      if (!mimeType) {
        throw new Error('No supported media recording MIME type found');
      }
      
      const mediaRecorder = new MediaRecorder(stream, {
        mimeType,
        audioBitsPerSecond: 128000
      });
      mediaRecorderRef.current = mediaRecorder;
      
      mediaRecorder.ondataavailable = (event) => {
        if (event.data.size > 0) {
          setAudioChunks((prevChunks) => [...prevChunks, event.data]);
        }
      };
      
      mediaRecorder.start(AUDIO_CONFIG.CHUNK_INTERVAL);
      setIsRecording(true);
      monitorAudioLevel();
      
    } catch (err) {
      console.error("Error accessing microphone:", err);
      alert('Error accessing microphone. Please check permissions.');
    }
  };

  const stopRecording = () => {
    if (mediaRecorderRef.current && streamRef.current) {
      mediaRecorderRef.current.stop();
      streamRef.current.getTracks().forEach(track => track.stop());
      
      if (audioChunks.length > 0) {
        saveAndSendAudio(audioChunks);
      }
      
      clearTimeout(silenceTimeoutRef.current);
      setIsRecording(false);
      setSilenceDetected(false);
      setSilenceStartTime(null);
      setAudioChunks([]);
      
      if (audioContextRef.current) {
        audioContextRef.current.close();
      }
    }
  };

  const toggleRecording = () => {
    if (isRecording) {
      stopRecording();
    } else {
      startRecording();
    }
  };

  const monitorAudioLevel = () => {
    if (!analyserRef.current || !isRecording) return;
    
    const dataArray = new Uint8Array(analyserRef.current.frequencyBinCount);
    analyserRef.current.getByteFrequencyData(dataArray);
    
    const average = dataArray.reduce((a, b) => a + b, 0) / dataArray.length;
    const dB = 20 * Math.log10(average / 255);
    
    if (dB < AUDIO_CONFIG.SILENCE_THRESHOLD) {
      if (!silenceDetected) {
        setSilenceDetected(true);
        setSilenceStartTime(Date.now());
      } else if (Date.now() - silenceStartTime > AUDIO_CONFIG.SILENCE_DURATION) {
        saveAndSendAudio(audioChunks);
        setSilenceStartTime(Date.now());
      }
    } else {
      setSilenceDetected(false);
      setSilenceStartTime(null);
    }
    
    requestAnimationFrame(monitorAudioLevel);
  };

  const saveAndSendAudio = async (chunks) => {
    if (!chunks || chunks.length === 0) return;
    
    try {
      setIsLoading(true);
      const wavBlob = await convertToWav(chunks, audioContextRef.current?.sampleRate || AUDIO_CONFIG.SAMPLE_RATE);
      console.log('WAV blob created:', wavBlob.size, 'bytes');
      
      const formData = new FormData();
      formData.append('audio', wavBlob, `recording_${Date.now()}.wav`);
      
      const response = await fetch(AUDIO_CONFIG.BACKEND_URL, {
        method: 'POST',
        body: formData,
      });
      
      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }
      
      console.log('Audio sent successfully');
      setAudioChunks([]);
      
      // Poll for transcription and TTS
      const result = await response.json();
      if (result.transcription_file) {
        await pollTranscription(result.transcription_file);
      }
    } catch (error) {
      console.error('Error processing/sending audio:', error);
    } finally {
      setIsLoading(false);
    }
  };

  const pollTranscription = async (filename) => {
    const maxAttempts = 30;
    let attempts = 0;
    
    while (attempts < maxAttempts) {
      try {
        const response = await fetch(`${AUDIO_CONFIG.BACKEND_URL}/transcription/${filename}`);
        if (response.ok) {
          const data = await response.json();
          setTranscription(data);
          return;
        }
      } catch (error) {
        console.error('Polling error:', error);
      }
      attempts++;
      await new Promise(resolve => setTimeout(resolve, 1000));
    }
  };

  const playTTSAudio = async () => {
    if (!transcription?.tts_audio) return;
    
    try {
      if (!audioPlayerRef.current) {
        audioPlayerRef.current = new Audio(`${AUDIO_CONFIG.BACKEND_URL}/tts-audio/${transcription.tts_audio}`);
      }
      
      setIsPlaying(true);
      await audioPlayerRef.current.play();
      
      audioPlayerRef.current.onended = () => {
        setIsPlaying(false);
      };
    } catch (error) {
      console.error('Error playing TTS:', error);
      setIsPlaying(false);
    }
  };

  useEffect(() => {
    return () => {
      if (isRecording) {
        stopRecording();
      }
    };
  }, []);

  return (
    <div className="audio-recorder">
      <button 
        className={`record-button ${isRecording ? 'recording' : ''}`}
        onClick={toggleRecording}
      >
        {isRecording ? <FaMicrophoneSlash /> : <FaMicrophone />}
      </button>
      <div className="status">
        {isRecording ? (
          <span className="recording-indicator">
            Recording {silenceDetected ? '(Silence detected)' : ''}
          </span>
        ) : (
          <span>Click to start recording</span>
        )}
      </div>
      
      {transcription && (
        <div className="transcription-section">
          <p className="transcription-text">{transcription.full_text}</p>
          <button 
            className={`tts-button ${isPlaying ? 'playing' : ''}`}
            onClick={playTTSAudio}
            disabled={isPlaying || isLoading}
          >
            {isLoading ? <FaSpinner className="spinner" /> : <FaPlay />}
            {isPlaying ? 'Playing...' : 'Play TTS'}
          </button>
        </div>
      )}
    </div>
  );
};

export default AudioRecorder;
