export const AUDIO_CONFIG = {
  SILENCE_THRESHOLD: -50,        // Adjusted for better silence detection
  SILENCE_DURATION: 3000,        // 3 seconds
  CHUNK_INTERVAL: 1000,         // 1 second chunks
  BACKEND_URL: 'http://localhost:5000/upload-audio',
  MIN_DECIBELS: -90,
  MAX_DECIBELS: -10,
  SMOOTHING_TIME_CONSTANT: 0.85
};
